import React,{useState,useEffect} from 'react';
import './details.css';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {useParams} from 'react-router-dom';

const base_url ="https://nykaa-api-jfl3.onrender.com";

const DetailDisplay = ()=>{

    let params = useParams();

    let [productDetails, setProductDetails] = useState();
    useEffect(()=>{
        let prodid = params.productId;
        axios.get(`${base_url}/productId?productId=${prodid}`)
        .then((res)=>{
            setProductDetails(res.data)
        })
    },[])

    // let prodId = searchParams.getAll('id');

    // const productDetail = async()=>{
    //     const pdata = await axios.get(`${base_url}/details/${prodId}`)
    //     setProductDetails(pdata.data)
    // }

    // useEffect(()=>{
    //     productDetail()
    // },[])


    return(
        <>
        <div className='main-detail-div'>
            <div className='details-div'>
               <div className='detail-img-div'>
                    <img className="detail-image" src={productDetails.item.product_img} alt={productDetails.item.product_name}/>
               </div>
               <div className='details'>
                <p className='detail-para'>{productDetails.item.product_name}</p>
               </div>
            </div>
        </div>
        </>
    )

}

    


export default DetailDisplay;
