import React ,{useState,useEffect} from "react";
import './Shop.css';
import ShopDisplay from './ShopDisplay';

const base_url ="https://nykaa-api-jfl3.onrender.com"
const Shop =()=>{

    const [faishontype,setFaishontype] = useState();

    useEffect(()=>{
        fetch(`${base_url}/nykaaFaishon`, {method:'GET'})
        .then((res)=>res.json())
        .then((data)=>{
            setFaishontype(data)
        })
    },[])
    // console.log(faishontype);

    const[categorytype,setCategorytype] = useState();

    useEffect(()=>{
        fetch(`{base_url}/categories` ,{method:'GET'})
        .then((res)=>res.json())
        .then((data)=>{
            setCategorytype(data)
        })
    },[])

    return(

       <>
                <div class="home-div">
                     {/* <h2 class="main-heading1">Best in Beauty</h2>
                        <div class="first-mainDiv">
                            <div class="first-mainDiv-cards">
                                <img src="https://images-static.nykaa.com/uploads/4ee9cff1-40b6-4959-9a51-e95adff8e3d2.jpg?tr=w-600,cm-pad_resize"></img>
                            </div>
                        </div> */}
                        <div className="mainheading"><h2 className="mainheading">Nykaa Faishon</h2></div>
                        <div class="nykaa-faishon">
                            <ShopDisplay faishonData={faishontype}/>                
                        </div>
                    
                        <div class="categories">  
                            <ShopDisplay categoryData={categorytype}/> 
                             {/* <div className="category-heading">
                                    <h3>Haircare</h3>
                                </div>*/}      
                         </div> 
                </div>                      
       </>
     
    )
}
export default Shop;
