import React,{useState,useEffect} from 'react';
import './details.css';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {searchParams, useSearchParams} from 'react-router-dom';

const base_url ="https://nykaa-api-jfl3.onrender.com";

const DetailDisplay = ()=>{

    let [searchParams] = useSearchParams();

    let [productDetails, setProductDetails] = useState();

    let prodId = searchParams.getAll('id');

    const productDetail = async()=>{
        const pdata = await axios.get(`${base_url}/details/id=${prodId}`)
        setProductDetails(pdata.data)
    }

    useEffect(()=>{
        productDetail()
    },[])


    return(
        <>
        <div className='main-detail-div'>
            <div className='details-div'>
               <div className='detail-img-div'>
                    <img className="detail-image" src={productDetails.product_img} alt={productDetails.product_name}/>
               </div>
               <div className='details'>
                <p className='detail-para'>{productDetails.product_name}</p>
               </div>
            </div>
        </div>
        </>
    )

}

    


export default DetailDisplay;
